mapper.update:
UPDATE codes
column_descript = #{column_descript}, code_name = #{code_name}, orderby = #{orderby}, apply = #{apply},
WHERE cd_id = #{cd_id}

mapper.delete:
        delete from fcms


                 and fcm_id = #{fcm_id} 
                 and user_id = #{user_id} 
                 and fcm_token = #{fcm_token} 

					and 1 = 2

mapper.update:
UPDATE users
assignment_id = #{assignment_id}, user_password = #{user_password}, user_name = #{user_name}, user_phone = #{user_phone}, user_type = #{user_type}, user_status = #{user_status}, update_time = now()
WHERE user_id = #{user_id}

mapper.select:
        select f.*
		from fcms f

             and f.fcm_id = #{fcm_id} 
             and f.user_id = #{user_id} 
             and f.fcm_token = #{fcm_token}

        select c.*
		from codes c

             and c.apply = #{apply} 
             and c.column_name = #{column_name} 

        order by c.column_name, c.orderby, c.code_id
