mapper.update:
UPDATE datas
data_json = #{data_json}, data_status = #{data_status}, user_id = -1, posted_by = #{user_id}, user_id = #{user_id}, assignment_id = #{assignment_id}, update_time = now()
WHERE data_id = #{data_id}

mapper.select:
        select count(*)
		from datas d

            and d.work_id = #{work_id}
             and d.data_id = #{data_id} 
             and d.user_id = #{user_id} 
             and d.data_status = #{data_status}

        select d.*  
		, fn_get_code_name('data_status', d.data_status) data_status_name, fn_get_user_name(d.user_id) user_name, fn_get_work_user_name(d.posted_by) work_user_name    
        from datas d

            and d.work_id = #{work_id}
             and d.data_id = #{data_id} 
             and d.user_id = #{user_id} 
             and d.data_status = #{data_status} 

        order by update_time DESC
         limit ${row_start}, ${row_count}

select count(*)      
from datas d

    and d.assignment_id = #{assignment_id}
    and d.data_status = #{data_status}

select count(*)      
from datas d

    and d.data_status = #{data_status}
    and d.user_id = #{user_id}
    and d.work_id = #{work_id}
    and d.assignment_id = #{assignment_id}

select d.*
, fn_get_code_name('data_status', d.data_status) data_status_name
from datas d

    json_extract(data_json, concat('$."',#{columnName},'"')) like concat('%',#{keyword},'%')
    and work_id = #{work_id}
    and data_status = #{data_status}

select w.*     
from works w

    and w.assignment_id = #{assignment_id}
